create database DbEmployeeManagementcreate table DepartmentMaster(Id int primary key identity(1,1),Departmentname varchar(100))Insert into DepartmentMaster values('Developer'),('Designer'),('Testing'),('HR')select*from DepartmentMastercreate table DesignationMaster(Id int primary key identity(1,1),DepartmentId int references DepartmentMaster(Id),DesignationName varchar(100))insert into DesignationMaster values (1,'React-Developer'),(1,'Angular-Developer'),(1,'Dot-Net-Developer'),(2,'Web Designer'),(2,'UI/UX Designer'),(3,'Manual Tester'),(3,'Automation Tester'),(4,'Recruiting'),(4,'Health and safety')select * from DesignationMastercreate table SalaryMaster(Id int primary key identity(1,1),DepartmentId int references DepartmentMaster(Id),DesignationId int references DesignationMaster(Id),BasicSalary int,Benefits int,Bonus int,OtherAllowance int)insert into SalaryMaster values (1,1,27000,2500,6000,6000),(1,2,27000,2750,5500,3250),(1,3,35000,4000,9000,5000),(2,4,30000,3500,7000,4000),(2,5,35000,4000,8500,4750),(3,6,25000,2500,4500,2750),(3,7,28000,3000,6000,3500)select*from SalaryMasterdrop table SalaryMastercreate table EmployeeMaster(Id int primary key identity(1,1),FirstName varchar(50),LastName varchar(50),DateOfBirth date,Email varchar(100),Username varchar(100),Password varchar(100),MobileNumber bigint,AddressLine1 varchar(100),AddressLine2 varchar(100),City varchar(100),State varchar(100),ZipCode int,HireDate date,IsActive bit not null default 1,DepartmentId int references DepartmentMaster(Id),DesignationId int references DesignationMaster(Id),CreatedDate date)select * from EmployeeMasterdelete from EmployeeMaster where EmployeeId = 1insert into EmployeeMaster(FirstName,LastName,DateOfBirth,Email,Username,Password,MobileNumber,AddressLine1,AddressLine2,City,State,ZipCode,CreatedDate) values ('Admin','manager','2002-03-30','admin@gmail.com','Administrator','administrator@123','6382830515','CG-VA